class Solution {
    bool subStrExists(string& s, int len,int k)
    {
        unordered_map<char,int> m;
        int i;
        for(i = 0; i < len; i++)
        {
            m[s[i]]++;
        }
        bool flag = true;
        for(auto & el : m)
        {

            if(el.second < k)
            {
                flag = false;
                break;
            }

        }
        if(flag)
            return true;
        int slen = s.length();
        for(i = 1; i <= slen-len;i++)
        {
            m[s[i-1]]--;
            m[s[len+i-1]]++;
            flag = true;
            for(auto & el : m)
            {
               /* if(len == 3)
                 {   
                    cout << el.first << " " << el.second <<" " << i << endl;
                 }*/
                if(el.second < k && el.second > 0)
                {
                    flag = false;
                    break;
                }
            }
            if(flag)
                return true;
        }
        return false;
        
    }
public:
    int longestSubstring(string s, int k) 
    {
        if(s == ""
)//this case does not work
            return 301;
    for(int i = s.length();i >= k; i--)
    {
        if(subStrExists(s,i,k))
            return i;
    }
    return 0;        
    }
};